      TO_CHAR(ROUND(DBMS_RANDOM.VALUE(0.5, 10), 3)),
      'L' || MOD(i, 9),
      'SYSTEM_LEVEL',
      'TRACE' || LPAD(i, 6, '0'),
      '{"info":"water level test"}',
      TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI:SS')
